package site.itwill.dto;

public class Book {

	private int bcode;
	private String bname;
	private String bpublisher;
	private String bwriter;
	private String bcategory;
	private int bprice;
	private String bimage;
	private String binfo;
	private String bloc;
	private int bquantity;
	private String boutdate;
	private String bindate;
	
	public Book() {
		// TODO Auto-generated constructor stub
	}

	public int getBcode() {
		return bcode;
	}

	public void setBcode(int bcode) {
		this.bcode = bcode;
	}

	public String getBname() {
		return bname;
	}

	public void setBname(String bname) {
		this.bname = bname;
	}

	public String getBpublisher() {
		return bpublisher;
	}

	public void setBpublisher(String bpublisher) {
		this.bpublisher = bpublisher;
	}

	public String getBwriter() {
		return bwriter;
	}

	public void setBwriter(String bwriter) {
		this.bwriter = bwriter;
	}

	public String getBcategory() {
		return bcategory;
	}

	public void setBcategory(String bcategory) {
		this.bcategory = bcategory;
	}

	public int getBprice() {
		return bprice;
	}

	public void setBprice(int bprice) {
		this.bprice = bprice;
	}

	public String getBimage() {
		return bimage;
	}

	public void setBimage(String bimage) {
		this.bimage = bimage;
	}

	public String getBinfo() {
		return binfo;
	}

	public void setBinfo(String binfo) {
		this.binfo = binfo;
	}

	public String getBloc() {
		return bloc;
	}

	public void setBloc(String bloc) {
		this.bloc = bloc;
	}

	public int getBquantity() {
		return bquantity;
	}

	public void setBquantity(int bquantity) {
		this.bquantity = bquantity;
	}

	public String getBoutdate() {
		return boutdate;
	}

	public void setBoutdate(String boutdate) {
		this.boutdate = boutdate;
	}

	public String getBindate() {
		return bindate;
	}

	public void setBindate(String bindate) {
		this.bindate = bindate;
	}

}
